mapper.select:
SELECT COUNT(*) AS totalCount
FROM tb_board
WHERE is_deleted = 0;

mapper.insert:
INSERT tb_board(
    title,
    content,
    created_by_user_idx
)
VALUES(
#{title},
#{content},
#{authorIdx}
)

mapper.update:
UPDATE tb_board
SET content = #{content}, updated_by_user_idx = #{authorIdx}
WHERE board_idx = #{boardIdx}

UPDATE tb_board
SET is_deleted = 1
WHERE board_idx = #{boardIdx}

UPDATE tb_board
SET view_count = view_count + 1
WHERE board_idx = #{boardIdx}
